import*as fruits from"./fruits.js";import*as modal from"./modal.js";const fruitsBlocks=document.querySelectorAll(".section__block");function checkWhichFruitClick(t){var o=t.target,t=t.target.id;setAttributeAnyModal(o),changeModalInner(t),modal.modalOpen()}function setAttributeAnyModal(t){t=t.querySelector("img").getAttribute("src");modal.modalImg.setAttribute("src",t)}function changeModalInner(t){modal.modalName.innerText=fruits.objectfFuits[t].name,modal.modalInfo.innerText=fruits.objectfFuits[t].info,modal.modalEat.innerText=fruits.objectfFuits[t].eat,modal.modalStore.innerText=fruits.objectfFuits[t].store,modal.modalPrompt.innerText=fruits.objectfFuits[t].prompt}fruitsBlocks.forEach(t=>{t.addEventListener("click",checkWhichFruitClick)});const copyPromptButton=document.querySelector("#copyPrompt");function copyPrompt(){var t=copyPromptButton.closest(".modal__prompt").querySelector("#prompt");navigator.clipboard.writeText(t.innerText),alert("Скопировано !")}copyPromptButton.addEventListener("click",copyPrompt);